import React from 'react';
import styles from './BenefitsBar.module.css';

const BenefitsBar = () => {
  const benefits = [
    {
      icon: 'boat-outline',
      title: 'Free Shipping',
      description: 'On Orders Over R900'
    },
    {
      icon: 'shield-checkmark-outline',
      title: 'Secure Payments',
      description: 'Protected by SSL'
    },
    {
      icon: 'arrow-undo-outline',
      title: 'Easy Returns',
      description: '30-Day Returns'
    },
    {
      icon: 'time-outline',
      title: 'Fast Delivery',
      description: 'Next Day in SA'
    },
    {
      icon: 'ticket-outline',
      title: 'Money Back',
      description: '30% on Orders Over R1200'
    }
  ];

  return (
    <div className={styles.benefitsBar}>
      <div className={styles.container}>
        {benefits.map((benefit, index) => (
          <div key={index} className={styles.benefit}>
            <div className={styles.iconWrapper}>
              <ion-icon name={benefit.icon}></ion-icon>
            </div>
            <div className={styles.content}>
              <h3>{benefit.title}</h3>
              <p>{benefit.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BenefitsBar;
