import React, { useState, useCallback, useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './Header.module.css';

const Header = () => {
  const [currency, setCurrency] = useState('ZAR');
  const [language, setLanguage] = useState('en-US');
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const searchTimeoutRef = useRef(null);
  const [cartCount] = useState(0);
  const [wishlistCount] = useState(0);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeAccordion, setActiveAccordion] = useState(null);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const dropdownTimeoutRef = useRef(null);
  const mobileMenuRef = useRef(null);
  const firstFocusableRef = useRef(null);
  const lastFocusableRef = useRef(null);
  const [focusedDropdownItem, setFocusedDropdownItem] = useState(null);

  // Debounced search function
  const handleSearch = useCallback((query) => {
    setSearchQuery(query);
    
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    if (query.trim()) {
      setIsSearching(true);
      searchTimeoutRef.current = setTimeout(() => {
        // TODO: Implement actual search API call here
        setSearchResults([
          { id: 1, name: "Nike Air Max", price: "R1,999" },
          { id: 2, name: "Air Jordan 1", price: "R2,499" },
          // Add more mock results as needed
        ]);
        setIsSearching(false);
      }, 500);
    } else {
      setSearchResults([]);
      setIsSearching(false);
    }
  }, []);

  // Close search results when clicking outside
  const searchRef = useRef(null);
  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setSearchResults([]);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    // Reset accordion when closing menu
    if (isMobileMenuOpen) {
      setActiveAccordion(null);
    }
  };

  const toggleAccordion = (index) => {
    setActiveAccordion(activeAccordion === index ? null : index);
  };

  // Handle dropdown hover with delay
  const handleDropdownEnter = (index) => {
    if (dropdownTimeoutRef.current) {
      clearTimeout(dropdownTimeoutRef.current);
    }
    setActiveDropdown(index);
  };

  const handleDropdownLeave = () => {
    dropdownTimeoutRef.current = setTimeout(() => {
      setActiveDropdown(null);
    }, 200);
  };

  // Handle mobile menu keyboard navigation
  const handleMobileMenuKeyDown = (e) => {
    if (e.key === 'Escape' && isMobileMenuOpen) {
      toggleMobileMenu();
    }
  };

  // Add keyboard event listener
  React.useEffect(() => {
    document.addEventListener('keydown', handleMobileMenuKeyDown);
    return () => document.removeEventListener('keydown', handleMobileMenuKeyDown);
  }, [isMobileMenuOpen]);

  // Focus trap for mobile menu
  const handleTabKey = (e) => {
    if (!isMobileMenuOpen) return;

    const firstFocusable = firstFocusableRef.current;
    const lastFocusable = lastFocusableRef.current;

    if (e.key === 'Tab') {
      if (e.shiftKey) { // Shift + Tab
        if (document.activeElement === firstFocusable) {
          e.preventDefault();
          lastFocusable.focus();
        }
      } else { // Tab
        if (document.activeElement === lastFocusable) {
          e.preventDefault();
          firstFocusable.focus();
        }
      }
    }
  };

  // Set up focus trap when mobile menu opens
  React.useEffect(() => {
    if (isMobileMenuOpen) {
      // Store last focused element
      const lastFocused = document.activeElement;
      
      // Focus first element in mobile menu
      firstFocusableRef.current?.focus();

      // Restore focus when menu closes
      return () => lastFocused?.focus();
    }
  }, [isMobileMenuOpen]);

  // Handle dropdown keyboard navigation
  const handleDropdownKeyDown = (e, index, items) => {
    const currentIndex = focusedDropdownItem?.index || 0;
    const isDropdown = items && items.length > 0;

    switch (e.key) {
      case 'Enter':
      case ' ':
        e.preventDefault();
        if (isDropdown) {
          setActiveDropdown(activeDropdown === index ? null : index);
          setFocusedDropdownItem({ index: 0, parentIndex: index });
        }
        break;

      case 'Escape':
        if (activeDropdown !== null) {
          e.preventDefault();
          setActiveDropdown(null);
          setFocusedDropdownItem(null);
        }
        break;

      case 'ArrowDown':
        if (isDropdown) {
          e.preventDefault();
          const nextIndex = (currentIndex + 1) % items.length;
          setFocusedDropdownItem({ index: nextIndex, parentIndex: index });
          document.querySelector(`[data-dropdown-item="${index}-${nextIndex}"]`)?.focus();
        }
        break;

      case 'ArrowUp':
        if (isDropdown) {
          e.preventDefault();
          const prevIndex = (currentIndex - 1 + items.length) % items.length;
          setFocusedDropdownItem({ index: prevIndex, parentIndex: index });
          document.querySelector(`[data-dropdown-item="${index}-${prevIndex}"]`)?.focus();
        }
        break;

      case 'ArrowRight':
        e.preventDefault();
        const nextMenu = (index + 1) % menuItems.length;
        setActiveDropdown(null);
        setFocusedDropdownItem(null);
        document.querySelector(`[data-menu-item="${nextMenu}"]`)?.focus();
        break;

      case 'ArrowLeft':
        e.preventDefault();
        const prevMenu = (index - 1 + menuItems.length) % menuItems.length;
        setActiveDropdown(null);
        setFocusedDropdownItem(null);
        document.querySelector(`[data-menu-item="${prevMenu}"]`)?.focus();
        break;

      default:
        break;
    }
  };

  // Update renderDesktopMenuItem to include keyboard navigation
  const renderDesktopMenuItem = (item, index) => {
    const isDropdown = item.dropdown;
    const isActive = activeDropdown === index;
    const dropdownItems = item.categories?.[0]?.links || [];

    return (
      <li 
        key={item.title}
        className={styles.menuItem}
        onMouseEnter={() => isDropdown && handleDropdownEnter(index)}
        onMouseLeave={handleDropdownLeave}
      >
        <Link
          to={item.path || '#'}
          className={styles.menuTitle}
          role={isDropdown ? 'button' : undefined}
          aria-haspopup={isDropdown ? 'true' : undefined}
          aria-expanded={isDropdown ? isActive : undefined}
          data-menu-item={index}
          tabIndex={0}
          onKeyDown={(e) => handleDropdownKeyDown(e, index, dropdownItems)}
        >
          {item.title}
          {isDropdown && (
            <span className={styles.dropdownIndicator} aria-hidden="true">
              <ion-icon name="chevron-down-outline"></ion-icon>
            </span>
          )}
        </Link>

        {isDropdown && (
          <div 
            className={`${styles.dropdownPanel} ${isActive ? styles.active : ''}`}
            role="menu"
            aria-label={`${item.title} submenu`}
          >
            <ul className={styles.dropdownList}>
              {dropdownItems.map((link, itemIndex) => (
                <li key={link.title}>
                  <Link
                    to={link.path}
                    className={styles.dropdownLink}
                    data-dropdown-item={`${index}-${itemIndex}`}
                    tabIndex={isActive ? 0 : -1}
                    onKeyDown={(e) => handleDropdownKeyDown(e, index, dropdownItems)}
                  >
                    {link.title}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        )}
      </li>
    );
  };

  // Menu items data
  const menuItems = [
    { title: 'Home', path: '/' },
    {
      title: 'Sneaker Categories',
      dropdown: true,
      categories: [
        {
          title: "Men's Sneakers",
          links: [
            { title: 'Running', path: '/category/mens/running' },
            { title: 'Casual', path: '/category/mens/casual' },
            { title: 'High-Tops', path: '/category/mens/high-tops' },
            { title: 'Basketball', path: '/category/mens/basketball' },
            { title: 'Limited Edition', path: '/category/mens/limited' },
          ],
          image: '/assets/images/nikedunkbanner.webp'
        },
        // ... other categories
      ]
    },
    { title: 'New Arrivals', path: '/new-arrivals' },
    { title: 'Trending', path: '/trending' },
    { title: 'Deals', path: '/deals' },
    { title: 'Blog', path: '/blog' },
  ];

  // Update mobile menu to include focus trap
  const renderMobileMenu = () => (
    <nav 
      ref={mobileMenuRef}
      className={`${styles.mobileNav} ${isMobileMenuOpen ? styles.active : ''}`}
      role="navigation"
      aria-label="Mobile navigation"
      aria-hidden={!isMobileMenuOpen}
      onKeyDown={handleTabKey}
    >
      <button 
        ref={firstFocusableRef}
        className={styles.menuCloseBtn} 
        onClick={toggleMobileMenu}
      >
        <ion-icon name="close-outline"></ion-icon>
      </button>

      {/* ... existing mobile menu content ... */}

      <button
        ref={lastFocusableRef}
        className={styles.menuCloseBtn}
        onClick={toggleMobileMenu}
      >
        Close Menu
      </button>
    </nav>
  );

  // Add keyboard event listeners
  React.useEffect(() => {
    document.addEventListener('keydown', handleTabKey);
    return () => document.removeEventListener('keydown', handleTabKey);
  }, [isMobileMenuOpen]);

  return (
    <header className={styles.header} role="banner">
      {/* Header Top */}
      <div className={styles.headerTop}>
        <div className={styles.container}>
          <ul className={styles.headerSocialContainer}>
            <li>
              <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className={styles.socialLink}>
                <ion-icon name="logo-facebook"></ion-icon>
              </a>
            </li>
            <li>
              <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" className={styles.socialLink}>
                <ion-icon name="logo-twitter"></ion-icon>
              </a>
            </li>
            <li>
              <a href="https://instagram.com" target="_blank" rel="noopener noreferrer" className={styles.socialLink}>
                <ion-icon name="logo-instagram"></ion-icon>
              </a>
            </li>
            <li>
              <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className={styles.socialLink}>
                <ion-icon name="logo-linkedin"></ion-icon>
              </a>
            </li>
          </ul>

          <div className={styles.headerAlertNews}>
            <p>
              <b>Free Shipping</b>
              This Week Order Over - 1899,99 rands
            </p>
          </div>

          <div className={styles.headerTopActions}>
            <select 
              value={currency}
              onChange={(e) => setCurrency(e.target.value)}
              className={styles.currencySelect}
            >
              <option value="ZAR">ZAR &#82;</option>
              <option value="USD">USD &#36;</option>
            </select>

            <select
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
              className={styles.languageSelect}
            >
              <option value="en-US">English</option>
            </select>
          </div>
        </div>
      </div>

      {/* Header Main */}
      <div className={styles.headerMain}>
        <div className={styles.container}>
          <Link to="/" className={styles.headerLogo}>
            <img 
              src="/assets/images/logo/SneakerAlotLogo.svg" 
              alt="SneakerAlot logo" 
              width="200" 
              height="200"
            />
          </Link>

          <div className={styles.headerSearchContainer} ref={searchRef}>
            <input
              type="search"
              name="search"
              className={styles.searchField}
              placeholder="Search for sneakers..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
              aria-label="Search products"
            />

            <button 
              className={`${styles.searchBtn} ${isSearching ? styles.searching : ''}`}
              aria-label="Search"
            >
              <ion-icon name={isSearching ? "reload-outline" : "search-outline"}></ion-icon>
            </button>

            {/* Search Results Dropdown */}
            {searchResults.length > 0 && (
              <div className={styles.searchResults}>
                {searchResults.map((result) => (
                  <Link 
                    key={result.id}
                    to={`/product/${result.id}`}
                    className={styles.searchResultItem}
                    onClick={() => setSearchResults([])}
                  >
                    <span className={styles.productName}>{result.name}</span>
                    <span className={styles.productPrice}>{result.price}</span>
                  </Link>
                ))}
              </div>
            )}
          </div>

          <div className={styles.headerUserActions}>
            <Link to="/account" className={styles.actionBtn}>
              <ion-icon name="person-outline"></ion-icon>
            </Link>

            <Link to="/wishlist" className={styles.actionBtn}>
              <ion-icon name="heart-outline"></ion-icon>
              {wishlistCount > 0 && (
                <span className={styles.count}>{wishlistCount}</span>
              )}
            </Link>

            <Link to="/cart" className={styles.actionBtn}>
              <ion-icon name="bag-handle-outline"></ion-icon>
              {cartCount > 0 && (
                <span className={styles.count}>{cartCount}</span>
              )}
            </Link>
          </div>
        </div>
      </div>

      {/* Desktop Navigation Menu */}
      <nav 
        className={styles.desktopNav} 
        role="navigation" 
        aria-label="Main navigation"
      >
        <div className={styles.container}>
          <ul className={styles.desktopMenuList}>
            {menuItems.map((item, index) => renderDesktopMenuItem(item, index))}
          </ul>
        </div>
      </nav>

      {/* Mobile Bottom Navigation */}
      <div className={styles.mobileBottomNav}>
        <button className={styles.actionBtn} onClick={toggleMobileMenu}>
          <ion-icon name="menu-outline"></ion-icon>
        </button>

        <Link to="/cart" className={styles.actionBtn}>
          <ion-icon name="bag-handle-outline"></ion-icon>
          {cartCount > 0 && <span className={styles.count}>{cartCount}</span>}
        </Link>

        <Link to="/" className={styles.actionBtn}>
          <ion-icon name="home-outline"></ion-icon>
        </Link>

        <Link to="/wishlist" className={styles.actionBtn}>
          <ion-icon name="heart-outline"></ion-icon>
          {wishlistCount > 0 && <span className={styles.count}>{wishlistCount}</span>}
        </Link>

        <button className={styles.actionBtn} onClick={toggleMobileMenu}>
          <ion-icon name="grid-outline"></ion-icon>
        </button>
      </div>

      {renderMobileMenu()}

      {/* Overlay */}
      {isMobileMenuOpen && (
        <div 
          className={styles.overlay} 
          onClick={toggleMobileMenu}
          role="presentation"
          aria-hidden="true"
        ></div>
      )}
    </header>
  );
};

export default Header;
