import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import styles from './Hero.module.css';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  const slides = [
    {
      id: 1,
      title: "Summer Collection 2024",
      subtitle: "Step into Style",
      description: "Discover the latest trends in sneaker fashion",
      cta: "Shop Now",
      image: "/images/hero/slide1.jpg",
      color: "#FF4D4D"
    },
    {
      id: 2,
      title: "Limited Edition",
      subtitle: "Exclusive Drops",
      description: "Get your hands on rare and exclusive sneakers",
      cta: "View Collection",
      image: "/images/hero/slide2.jpg",
      color: "#4D7FFF"
    },
    {
      id: 3,
      title: "New Arrivals",
      subtitle: "Fresh Kicks",
      description: "Be the first to wear the newest releases",
      cta: "Explore Now",
      image: "/images/hero/slide3.jpg",
      color: "#FFB84D"
    }
  ];

  const nextSlide = useCallback(() => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  }, [slides.length]);

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  useEffect(() => {
    let interval;
    if (isAutoPlaying) {
      interval = setInterval(nextSlide, 5000);
    }
    return () => clearInterval(interval);
  }, [isAutoPlaying, nextSlide]);

  const handleMouseEnter = () => setIsAutoPlaying(false);
  const handleMouseLeave = () => setIsAutoPlaying(true);

  return (
    <section 
      className={styles.hero}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className={styles.slider}>
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`${styles.slide} ${index === currentSlide ? styles.active : ''}`}
            style={{
              '--slide-color': slide.color,
              transform: `translateX(${(index - currentSlide) * 100}%)`
            }}
          >
            <div className={styles.content}>
              <span className={styles.subtitle}>{slide.subtitle}</span>
              <h1 className={styles.title}>{slide.title}</h1>
              <p className={styles.description}>{slide.description}</p>
              <Link to="/shop" className={styles.cta}>
                {slide.cta}
                <ion-icon name="arrow-forward-outline"></ion-icon>
              </Link>
            </div>
            <div 
              className={styles.imageWrapper}
              style={{ backgroundImage: `url(${slide.image})` }}
            >
              <div className={styles.overlay} />
            </div>
          </div>
        ))}
      </div>

      <div className={styles.controls}>
        <button 
          className={`${styles.arrow} ${styles.prev}`}
          onClick={prevSlide}
          aria-label="Previous slide"
        >
          <ion-icon name="chevron-back-outline"></ion-icon>
        </button>
        <div className={styles.indicators}>
          {slides.map((_, index) => (
            <button
              key={index}
              className={`${styles.indicator} ${index === currentSlide ? styles.active : ''}`}
              onClick={() => goToSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
        <button 
          className={`${styles.arrow} ${styles.next}`}
          onClick={nextSlide}
          aria-label="Next slide"
        >
          <ion-icon name="chevron-forward-outline"></ion-icon>
        </button>
      </div>
    </section>
  );
};

export default Hero;
