import React, { useState, useCallback, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from './Header.module.css';

const Header = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isHeaderHidden, setIsHeaderHidden] = useState(false);
  const [lastScrollY, setLastScrollY] = useState(0);

  // Handle scroll behavior
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      const scrollDifference = currentScrollY - lastScrollY;

      if (currentScrollY < 50) {
        setIsHeaderHidden(false);
        return;
      }

      if (scrollDifference > 0 && !isHeaderHidden && currentScrollY > 60) {
        setIsHeaderHidden(true);
      } else if (scrollDifference < 0 && isHeaderHidden) {
        setIsHeaderHidden(false);
      }

      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [isHeaderHidden, lastScrollY]);

  const toggleMobileMenu = useCallback(() => {
    setIsMobileMenuOpen(prev => !prev);
    if (!isMobileMenuOpen) {
      setIsSearchOpen(false);
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
  }, [isMobileMenuOpen]);

  const toggleSearch = useCallback(() => {
    setIsSearchOpen(prev => !prev);
    if (!isSearchOpen) {
      setIsMobileMenuOpen(false);
      document.body.style.overflow = '';
    }
  }, [isSearchOpen]);

  useEffect(() => {
    return () => {
      document.body.style.overflow = '';
    };
  }, []);

  useEffect(() => {
    document.body.style.paddingTop = '60px';
    return () => {
      document.body.style.paddingTop = '0';
    };
  }, []);

  return (
    <header className={`${styles.header} ${isHeaderHidden ? styles.hidden : ''}`}>
      <div className={styles.container}>
        {/* Logo */}
        <Link to="/" className={styles.logo}>
          SneakerAlot
        </Link>

        {/* Desktop Navigation */}
        <nav className={styles.mainNav}>
          <ul>
            <li><Link to="/new-arrivals">New Arrivals</Link></li>
            <li><Link to="/men">Men</Link></li>
            <li><Link to="/women">Women</Link></li>
            <li><Link to="/kids">Kids</Link></li>
            <li><Link to="/sale">Sale</Link></li>
          </ul>
        </nav>

        {/* Mobile Search */}
        <div className={styles.searchContainer}>
          <input
            type="text"
            className={styles.searchInput}
            placeholder="Search"
          />
          <button className={styles.searchButton}>
            <ion-icon name="search-outline"></ion-icon>
          </button>
        </div>

        {/* Mobile Actions */}
        <div className={styles.mobileActions}>
          <Link to="/account" className={styles.iconButton} aria-label="Account">
            <ion-icon name="person-outline"></ion-icon>
          </Link>
          <Link to="/cart" className={styles.iconButton} aria-label="Cart">
            <ion-icon name="bag-outline"></ion-icon>
            <span className={styles.cartBadge}>0</span>
          </Link>
          <button 
            className={styles.mobileMenuButton}
            onClick={toggleMobileMenu}
            aria-expanded={isMobileMenuOpen}
            aria-label="Menu"
          >
            <span className={styles.hamburgerIcon}>
              <span className={`${styles.hamburgerBar} ${isMobileMenuOpen ? styles.active : ''}`}></span>
            </span>
          </button>
        </div>

        {/* Desktop Actions */}
        <div className={styles.desktopActions}>
          <div className={styles.searchContainer}>
            <input
              type="text"
              className={styles.searchInput}
              placeholder="Search"
            />
            <button className={styles.searchButton}>
              <ion-icon name="search-outline"></ion-icon>
            </button>
          </div>
          <Link to="/wishlist" className={styles.iconButton}>
            <ion-icon name="heart-outline"></ion-icon>
          </Link>
          <Link to="/cart" className={styles.iconButton}>
            <ion-icon name="bag-outline"></ion-icon>
            <span className={styles.cartBadge}>0</span>
          </Link>
          <Link to="/account" className={styles.iconButton}>
            <ion-icon name="person-outline"></ion-icon>
          </Link>
        </div>
      </div>

      {/* Mobile Menu */}
      <nav className={`${styles.mobileMenu} ${isMobileMenuOpen ? styles.active : ''}`}>
        <ul>
          <li><Link to="/new-arrivals" onClick={() => setIsMobileMenuOpen(false)}>New Arrivals</Link></li>
          <li><Link to="/men" onClick={() => setIsMobileMenuOpen(false)}>Men</Link></li>
          <li><Link to="/women" onClick={() => setIsMobileMenuOpen(false)}>Women</Link></li>
          <li><Link to="/kids" onClick={() => setIsMobileMenuOpen(false)}>Kids</Link></li>
          <li><Link to="/sale" onClick={() => setIsMobileMenuOpen(false)}>Sale</Link></li>
        </ul>
      </nav>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div className={styles.mobileOverlay} onClick={toggleMobileMenu}></div>
      )}
    </header>
  );
};

export default Header;
