import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './SearchBar.module.css';

// Mock data for demonstration - replace with API calls later
const mockSuggestions = {
  recent: [
    { id: 1, type: 'recent', text: 'Nike Air Max' },
    { id: 2, type: 'recent', text: 'Jordan 1 High' },
    { id: 3, type: 'recent', text: 'Yeezy Boost' }
  ],
  popular: [
    { id: 4, type: 'popular', text: 'Ultra Boost', searches: 2345 },
    { id: 5, type: 'popular', text: 'Air Force 1', searches: 2100 },
    { id: 6, type: 'popular', text: 'New Balance 550', searches: 1890 }
  ],
  categories: [
    { id: 7, type: 'category', text: 'Running Shoes', count: 156 },
    { id: 8, type: 'category', text: 'Basketball Shoes', count: 142 },
    { id: 9, type: 'category', text: 'Lifestyle Sneakers', count: 238 }
  ],
  brands: [
    { id: 10, type: 'brand', text: 'Nike', products: 450 },
    { id: 11, type: 'brand', text: 'Adidas', products: 380 },
    { id: 12, type: 'brand', text: 'Jordan', products: 220 }
  ]
};

const SearchBar = ({ onClose, isExpanded = false }) => {
  const [query, setQuery] = useState('');
  const [isActive, setIsActive] = useState(isExpanded);
  const [suggestions, setSuggestions] = useState(mockSuggestions);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const searchRef = useRef(null);
  const inputRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsActive(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  useEffect(() => {
    if (isActive && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isActive]);

  const handleFocus = () => {
    setIsActive(true);
  };

  const handleChange = (e) => {
    const value = e.target.value;
    setQuery(value);
    // Here you would typically make an API call for suggestions
    // For now, we'll just filter the mock data
    if (value.length > 0) {
      const filtered = {
        recent: mockSuggestions.recent.filter(item => 
          item.text.toLowerCase().includes(value.toLowerCase())
        ),
        popular: mockSuggestions.popular.filter(item =>
          item.text.toLowerCase().includes(value.toLowerCase())
        ),
        categories: mockSuggestions.categories.filter(item =>
          item.text.toLowerCase().includes(value.toLowerCase())
        ),
        brands: mockSuggestions.brands.filter(item =>
          item.text.toLowerCase().includes(value.toLowerCase())
        )
      };
      setSuggestions(filtered);
    } else {
      setSuggestions(mockSuggestions);
    }
  };

  const handleKeyDown = (e) => {
    // Calculate total number of suggestions
    const allSuggestions = [
      ...suggestions.recent,
      ...suggestions.popular,
      ...suggestions.categories,
      ...suggestions.brands
    ];
    
    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setSelectedIndex(prev => 
          prev < allSuggestions.length - 1 ? prev + 1 : 0
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setSelectedIndex(prev => 
          prev > 0 ? prev - 1 : allSuggestions.length - 1
        );
        break;
      case 'Enter':
        e.preventDefault();
        if (selectedIndex >= 0) {
          handleSuggestionClick(allSuggestions[selectedIndex]);
        } else {
          handleSearch();
        }
        break;
      case 'Escape':
        setIsActive(false);
        break;
      default:
        break;
    }
  };

  const handleSearch = () => {
    if (query.trim()) {
      navigate(`/search?q=${encodeURIComponent(query.trim())}`);
      setIsActive(false);
      if (onClose) onClose();
    }
  };

  const handleSuggestionClick = (suggestion) => {
    switch (suggestion.type) {
      case 'recent':
      case 'popular':
        setQuery(suggestion.text);
        handleSearch();
        break;
      case 'category':
        navigate(`/category/${suggestion.text.toLowerCase().replace(/\s+/g, '-')}`);
        break;
      case 'brand':
        navigate(`/brand/${suggestion.text.toLowerCase()}`);
        break;
      default:
        break;
    }
    setIsActive(false);
    if (onClose) onClose();
  };

  const clearSearch = () => {
    setQuery('');
    inputRef.current?.focus();
  };

  const renderSuggestionIcon = (type) => {
    switch (type) {
      case 'recent':
        return <ion-icon name="time-outline" />;
      case 'popular':
        return <ion-icon name="trending-up-outline" />;
      case 'category':
        return <ion-icon name="grid-outline" />;
      case 'brand':
        return <ion-icon name="bookmark-outline" />;
      default:
        return null;
    }
  };

  const renderSuggestionMeta = (suggestion) => {
    switch (suggestion.type) {
      case 'popular':
        return <span className={styles.meta}>{suggestion.searches} searches</span>;
      case 'category':
        return <span className={styles.meta}>{suggestion.count} items</span>;
      case 'brand':
        return <span className={styles.meta}>{suggestion.products} products</span>;
      default:
        return null;
    }
  };

  return (
    <div 
      className={`${styles.searchContainer} ${isActive ? styles.active : ''}`}
      ref={searchRef}
    >
      <div className={styles.searchBar}>
        <input
          ref={inputRef}
          type="text"
          value={query}
          onChange={handleChange}
          onFocus={handleFocus}
          onKeyDown={handleKeyDown}
          placeholder="Search for products, brands, and more..."
          className={styles.searchInput}
          aria-label="Search"
        />
        
        {query && (
          <button
            className={styles.clearButton}
            onClick={clearSearch}
            aria-label="Clear search"
          >
            <ion-icon name="close-outline" />
          </button>
        )}
        
        <button
          className={styles.searchButton}
          onClick={handleSearch}
          aria-label="Search"
        >
          <ion-icon name="search-outline" />
        </button>
      </div>

      {isActive && (
        <div className={styles.suggestionsPanel}>
          {/* Recent Searches */}
          {suggestions.recent.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Recent Searches</h3>
              {suggestions.recent.map((suggestion, index) => (
                <button
                  key={suggestion.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index ? styles.selected : ''
                  }`}
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  {renderSuggestionIcon(suggestion.type)}
                  <span className={styles.text}>{suggestion.text}</span>
                </button>
              ))}
            </div>
          )}

          {/* Popular Searches */}
          {suggestions.popular.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Popular Searches</h3>
              {suggestions.popular.map((suggestion, index) => (
                <button
                  key={suggestion.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index + suggestions.recent.length ? styles.selected : ''
                  }`}
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  {renderSuggestionIcon(suggestion.type)}
                  <span className={styles.text}>{suggestion.text}</span>
                  {renderSuggestionMeta(suggestion)}
                </button>
              ))}
            </div>
          )}

          {/* Categories */}
          {suggestions.categories.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Categories</h3>
              {suggestions.categories.map((suggestion, index) => (
                <button
                  key={suggestion.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index + suggestions.recent.length + suggestions.popular.length
                      ? styles.selected
                      : ''
                  }`}
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  {renderSuggestionIcon(suggestion.type)}
                  <span className={styles.text}>{suggestion.text}</span>
                  {renderSuggestionMeta(suggestion)}
                </button>
              ))}
            </div>
          )}

          {/* Brands */}
          {suggestions.brands.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Brands</h3>
              {suggestions.brands.map((suggestion, index) => (
                <button
                  key={suggestion.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index + suggestions.recent.length + 
                    suggestions.popular.length + suggestions.categories.length
                      ? styles.selected
                      : ''
                  }`}
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  {renderSuggestionIcon(suggestion.type)}
                  <span className={styles.text}>{suggestion.text}</span>
                  {renderSuggestionMeta(suggestion)}
                </button>
              ))}
            </div>
          )}

          {/* No Results */}
          {query && 
            !suggestions.recent.length && 
            !suggestions.popular.length && 
            !suggestions.categories.length && 
            !suggestions.brands.length && (
            <div className={styles.noResults}>
              <ion-icon name="search-outline" />
              <p>No results found for "{query}"</p>
              <span>Try checking your spelling or using different keywords</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
