import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { searchService } from '../../../services/searchService';
import styles from './SearchBar.module.css';

const SearchBar = ({ onClose, isExpanded = false }) => {
  const [query, setQuery] = useState('');
  const [isActive, setIsActive] = useState(isExpanded);
  const [searchHistory, setSearchHistory] = useState([]);
  const [trendingSearches, setTrendingSearches] = useState([]);
  const [personalizedSuggestions, setPersonalizedSuggestions] = useState([]);
  const [spellSuggestions, setSpellSuggestions] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const [isLoading, setIsLoading] = useState(false);
  const searchRef = useRef(null);
  const inputRef = useRef(null);
  const navigate = useNavigate();

  // Load initial data
  useEffect(() => {
    const loadInitialData = async () => {
      setSearchHistory(searchService.getSearchHistory());
      const trending = await searchService.getTrendingSearches();
      setTrendingSearches(trending);
      const personalized = await searchService.getPersonalizedSuggestions();
      setPersonalizedSuggestions(personalized);
    };

    loadInitialData();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsActive(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  useEffect(() => {
    if (isActive && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isActive]);

  const handleFocus = () => {
    setIsActive(true);
  };

  const handleChange = (e) => {
    const value = e.target.value;
    setQuery(value);
    setIsLoading(true);

    // Debounce spell checking
    const timer = setTimeout(() => {
      if (value.length >= 2) {
        const suggestions = searchService.getSpellSuggestions(value);
        setSpellSuggestions(suggestions);
      } else {
        setSpellSuggestions([]);
      }
      setIsLoading(false);
    }, 300);

    return () => clearTimeout(timer);
  };

  const handleKeyDown = (e) => {
    const allItems = [
      ...searchHistory,
      ...trendingSearches,
      ...personalizedSuggestions,
      ...spellSuggestions
    ];
    
    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setSelectedIndex(prev => 
          prev < allItems.length - 1 ? prev + 1 : 0
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setSelectedIndex(prev => 
          prev > 0 ? prev - 1 : allItems.length - 1
        );
        break;
      case 'Enter':
        e.preventDefault();
        if (selectedIndex >= 0) {
          handleSuggestionClick(allItems[selectedIndex]);
        } else {
          handleSearch();
        }
        break;
      case 'Escape':
        setIsActive(false);
        break;
      default:
        break;
    }
  };

  const handleSearch = () => {
    if (query.trim()) {
      searchService.addToHistory(query.trim());
      navigate(`/search?q=${encodeURIComponent(query.trim())}`);
      setIsActive(false);
      if (onClose) onClose();
    }
  };

  const handleSuggestionClick = (suggestion) => {
    if (suggestion.text) {
      setQuery(suggestion.text);
      searchService.addToHistory(suggestion.text);
      navigate(`/search?q=${encodeURIComponent(suggestion.text)}`);
    }
    setIsActive(false);
    if (onClose) onClose();
  };

  const clearHistory = () => {
    searchService.clearHistory();
    setSearchHistory([]);
  };

  const clearSearch = () => {
    setQuery('');
    setSpellSuggestions([]);
    inputRef.current?.focus();
  };

  return (
    <div 
      className={`${styles.searchContainer} ${isActive ? styles.active : ''}`}
      ref={searchRef}
    >
      <div className={styles.searchBar}>
        <input
          ref={inputRef}
          type="text"
          value={query}
          onChange={handleChange}
          onFocus={handleFocus}
          onKeyDown={handleKeyDown}
          placeholder="Search for products, brands, and more..."
          className={styles.searchInput}
          aria-label="Search"
        />
        
        {query && (
          <button
            className={styles.clearButton}
            onClick={clearSearch}
            aria-label="Clear search"
          >
            <ion-icon name="close-outline" />
          </button>
        )}
        
        <button
          className={styles.searchButton}
          onClick={handleSearch}
          aria-label="Search"
        >
          <ion-icon name={isLoading ? "refresh-outline" : "search-outline"} 
            className={isLoading ? styles.spinning : ''} 
          />
        </button>
      </div>

      {isActive && (
        <div className={styles.suggestionsPanel}>
          {/* Spell Suggestions */}
          {spellSuggestions.length > 0 && (
            <div className={styles.spellSuggestions}>
              <p>Did you mean:</p>
              {spellSuggestions.map((suggestion, index) => (
                <button
                  key={suggestion}
                  className={styles.spellSuggestion}
                  onClick={() => setQuery(suggestion)}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          )}

          {/* Search History */}
          {searchHistory.length > 0 && (
            <div className={styles.suggestionSection}>
              <div className={styles.sectionHeader}>
                <h3>Recent Searches</h3>
                <button
                  className={styles.clearHistoryButton}
                  onClick={clearHistory}
                >
                  Clear All
                </button>
              </div>
              {searchHistory.map((item, index) => (
                <button
                  key={item.timestamp}
                  className={`${styles.suggestion} ${
                    selectedIndex === index ? styles.selected : ''
                  }`}
                  onClick={() => handleSuggestionClick(item)}
                >
                  <ion-icon name="time-outline" />
                  <span className={styles.text}>{item.text}</span>
                  <span className={styles.timestamp}>
                    {searchService.formatRelativeTime(item.timestamp)}
                  </span>
                </button>
              ))}
            </div>
          )}

          {/* Trending Searches */}
          {trendingSearches.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Trending Now</h3>
              {trendingSearches.map((item, index) => (
                <button
                  key={item.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index + searchHistory.length ? styles.selected : ''
                  }`}
                  onClick={() => handleSuggestionClick(item)}
                >
                  <ion-icon name={item.trending ? "trending-up-outline" : "stats-chart-outline"} />
                  <span className={styles.text}>{item.text}</span>
                  <span className={styles.searches}>{item.searches.toLocaleString()} searches</span>
                </button>
              ))}
            </div>
          )}

          {/* Personalized Suggestions */}
          {personalizedSuggestions.length > 0 && (
            <div className={styles.suggestionSection}>
              <h3>Recommended for You</h3>
              {personalizedSuggestions.map((item, index) => (
                <button
                  key={item.id}
                  className={`${styles.suggestion} ${
                    selectedIndex === index + searchHistory.length + trendingSearches.length
                      ? styles.selected
                      : ''
                  }`}
                  onClick={() => handleSuggestionClick(item)}
                >
                  <ion-icon name="star-outline" />
                  <div className={styles.suggestionContent}>
                    <span className={styles.text}>{item.text}</span>
                    <span className={styles.reason}>{item.reason}</span>
                  </div>
                </button>
              ))}
            </div>
          )}

          {/* No Results */}
          {query && 
            !spellSuggestions.length &&
            !searchHistory.length && 
            !trendingSearches.length && 
            !personalizedSuggestions.length && (
            <div className={styles.noResults}>
              <ion-icon name="search-outline" />
              <p>No results found for "{query}"</p>
              <span>Try checking your spelling or using different keywords</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
