import React, { useState, useCallback } from 'react';
import { Link } from 'react-router-dom';
import styles from './Header.module.css';

const Header = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  
  // Mock data - replace with real data later
  const cartItemsCount = 0;
  const isAuthenticated = false;

  const toggleSearch = useCallback(() => {
    setIsSearchOpen(prev => !prev);
  }, []);

  const toggleMobileMenu = useCallback(() => {
    setIsMobileMenuOpen(prev => !prev);
  }, []);

  const toggleUserMenu = useCallback(() => {
    setIsUserMenuOpen(prev => !prev);
  }, []);

  return (
    <header className={styles.header}>
      <div className={styles.container}>
        {/* Mobile Menu Button */}
        <button 
          className={styles.mobileMenuButton}
          onClick={toggleMobileMenu}
          aria-expanded={isMobileMenuOpen}
          aria-label="Toggle menu"
        >
          <ion-icon name={isMobileMenuOpen ? "close-outline" : "menu-outline"} />
        </button>

        {/* Logo */}
        <Link to="/" className={styles.logo}>
          <img src="/assets/images/logo.png" alt="Sneakeralot" />
        </Link>

        {/* Main Navigation */}
        <nav 
          className={`${styles.mainNav} ${isMobileMenuOpen ? styles.active : ''}`}
          role="navigation"
          aria-label="Main navigation"
        >
          <ul>
            <li>
              <Link to="/new-arrivals">New Arrivals</Link>
            </li>
            <li>
              <Link to="/men">Men</Link>
            </li>
            <li>
              <Link to="/women">Women</Link>
            </li>
            <li>
              <Link to="/kids">Kids</Link>
            </li>
            <li>
              <Link to="/sale">Sale</Link>
            </li>
          </ul>
        </nav>

        {/* Right Section */}
        <div className={styles.rightSection}>
          {/* Search */}
          <div className={`${styles.searchContainer} ${isSearchOpen ? styles.active : ''}`}>
            <button 
              className={styles.searchToggle}
              onClick={toggleSearch}
              aria-expanded={isSearchOpen}
              aria-label="Toggle search"
            >
              <ion-icon name={isSearchOpen ? "close-outline" : "search-outline"} />
            </button>
            {isSearchOpen && (
              <div className={styles.searchBar}>
                <input
                  type="search"
                  placeholder="Search for products..."
                  aria-label="Search"
                />
                <button type="submit" aria-label="Submit search">
                  <ion-icon name="search-outline" />
                </button>
              </div>
            )}
          </div>

          {/* User Account */}
          <div className={styles.userContainer}>
            <button 
              className={styles.userButton}
              onClick={toggleUserMenu}
              aria-expanded={isUserMenuOpen}
              aria-label="User menu"
            >
              <ion-icon name="person-outline" />
            </button>
            {isUserMenuOpen && (
              <div className={styles.userMenu} role="menu">
                {isAuthenticated ? (
                  <>
                    <Link to="/account" role="menuitem">My Account</Link>
                    <Link to="/orders" role="menuitem">Orders</Link>
                    <Link to="/wishlist" role="menuitem">Wishlist</Link>
                    <button onClick={() => {/* handle logout */}} role="menuitem">
                      Logout
                    </button>
                  </>
                ) : (
                  <>
                    <Link to="/login" role="menuitem">Login</Link>
                    <Link to="/register" role="menuitem">Register</Link>
                  </>
                )}
              </div>
            )}
          </div>

          {/* Cart */}
          <Link to="/cart" className={styles.cartButton} aria-label={`Cart with ${cartItemsCount} items`}>
            <ion-icon name="bag-outline" />
            {cartItemsCount > 0 && (
              <span className={styles.cartCount}>{cartItemsCount}</span>
            )}
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
