import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Link } from 'react-router-dom';
import styles from './Hero.module.css';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const [direction, setDirection] = useState('next');
  const [touchStart, setTouchStart] = useState(null);
  const [touchEnd, setTouchEnd] = useState(null);
  const [isZoomed, setIsZoomed] = useState(false);
  const [progress, setProgress] = useState(0);
  const [zoomLevel, setZoomLevel] = useState(1);
  const [panPosition, setPanPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [activeHotspot, setActiveHotspot] = useState(null);
  const [touchMoveCount, setTouchMoveCount] = useState(0);
  const [pinchStart, setPinchStart] = useState(null);
  const touchTimeout = useRef(null);
  const sliderRef = useRef(null);
  const progressInterval = useRef(null);
  const imageRef = useRef(null);
  const [activeQuickView, setActiveQuickView] = useState(null);
  const [selectedSize, setSelectedSize] = useState(null);
  const [selectedColor, setSelectedColor] = useState(null);
  const [is360View, setIs360View] = useState(false);
  const [rotationDegree, setRotationDegree] = useState(0);
  const rotationRef = useRef(null);

  const slides = [
    {
      id: 1,
      title: "Summer Collection 2024",
      subtitle: "Step into Style",
      description: "Discover the latest trends in sneaker fashion with our curated summer collection",
      cta: "Shop Now",
      image: "/images/hero/slide1.jpg",
      color: "#FF4D4D",
      product: {
        name: "Air Max Pulse",
        price: 179.99,
        originalPrice: 199.99,
        sizes: ["US 7", "US 8", "US 9", "US 10", "US 11"],
        colors: [
          { name: "Obsidian", code: "#1a237e" },
          { name: "Crimson", code: "#d32f2f" },
          { name: "Platinum", code: "#e0e0e0" }
        ],
        images360: [
          "/images/hero/slide1.jpg",
          "/images/hero/slide1.jpg",
          "/images/hero/slide1.jpg"
        ],
        features: ["Responsive Cushioning", "Breathable Mesh", "Durable Outsole"]
      },
      hotspots: [
        {
          id: 'hs1',
          x: 25,
          y: 45,
          title: 'Premium Materials',
          description: 'Made with genuine leather and premium textiles',
          icon: 'diamond-outline',
          type: 'material'
        },
        {
          id: 'hs2',
          x: 65,
          y: 70,
          title: 'Comfort Technology',
          description: 'Advanced cushioning system for all-day comfort',
          icon: 'fitness-outline',
          type: 'feature',
          price: 179.99
        }
      ]
    },
    {
      id: 2,
      title: "Limited Edition Drops",
      subtitle: "Exclusive Collection",
      description: "Get your hands on rare and exclusive sneakers before they're gone",
      cta: "View Collection",
      image: "/images/hero/slide2.jpg",
      color: "#4D7FFF",
      product: {
        name: "Limited Edition Runner",
        price: 199.99,
        sizes: ["US 8", "US 9", "US 10"],
        colors: [
          { name: "Gold", code: "#FFD700" },
          { name: "Silver", code: "#C0C0C0" }
        ],
        images360: [
          "/images/hero/slide2.jpg",
          "/images/hero/slide2.jpg",
          "/images/hero/slide2.jpg"
        ],
        features: ["Limited Edition", "Numbered Series", "Certificate of Authenticity"]
      },
      hotspots: [
        {
          id: 'hs3',
          x: 35,
          y: 60,
          title: 'Limited Edition',
          description: 'Only 100 pairs available worldwide',
          icon: 'star-outline',
          type: 'feature'
        }
      ]
    },
    {
      id: 3,
      title: "New Arrivals",
      subtitle: "Fresh Kicks",
      description: "Be the first to wear the newest releases from top brands",
      cta: "Explore Now",
      image: "/images/hero/slide3.jpg",
      color: "#FFB84D",
      product: {
        name: "Fresh Release",
        price: 159.99,
        sizes: ["US 7", "US 8", "US 9", "US 10", "US 11"],
        colors: [
          { name: "White", code: "#FFFFFF" },
          { name: "Black", code: "#000000" }
        ],
        images360: [
          "/images/hero/slide3.jpg",
          "/images/hero/slide3.jpg",
          "/images/hero/slide3.jpg"
        ],
        features: ["New Release", "Modern Design", "Premium Quality"]
      }
    }
  ];

  const nextSlide = useCallback(() => {
    setDirection('next');
    setCurrentSlide((prev) => (prev + 1) % slides.length);
    setIsZoomed(false);
    setProgress(0);
  }, [slides.length]);

  const prevSlide = useCallback(() => {
    setDirection('prev');
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
    setIsZoomed(false);
    setProgress(0);
  }, [slides.length]);

  const goToSlide = (index) => {
    setDirection(index > currentSlide ? 'next' : 'prev');
    setCurrentSlide(index);
    setIsZoomed(false);
    setProgress(0);
  };

  useEffect(() => {
    let interval;
    if (isAutoPlaying && !isZoomed) {
      interval = setInterval(nextSlide, 5000);
      
      // Progress bar update
      if (progressInterval.current) {
        clearInterval(progressInterval.current);
      }
      progressInterval.current = setInterval(() => {
        setProgress(prev => {
          if (prev >= 100) return 0;
          return prev + 2;
        });
      }, 100);
    }
    return () => {
      clearInterval(interval);
      if (progressInterval.current) {
        clearInterval(progressInterval.current);
      }
    };
  }, [isAutoPlaying, nextSlide, isZoomed]);

  const handleMouseEnter = () => setIsAutoPlaying(false);
  const handleMouseLeave = () => {
    if (!isZoomed) {
      setIsAutoPlaying(true);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'ArrowLeft') {
      prevSlide();
    } else if (e.key === 'ArrowRight') {
      nextSlide();
    } else if (e.key === 'Escape' && isZoomed) {
      setIsZoomed(false);
    }
  };

  const handleZoomIn = () => {
    setZoomLevel(prev => Math.min(prev + 0.5, 3));
    setIsZoomed(true);
    setIsAutoPlaying(false);
  };

  const handleZoomOut = () => {
    setZoomLevel(prev => {
      const newZoom = Math.max(prev - 0.5, 1);
      if (newZoom === 1) {
        setIsZoomed(false);
        setPanPosition({ x: 0, y: 0 });
      }
      return newZoom;
    });
  };

  const handleMouseDown = (e) => {
    if (isZoomed) {
      setIsDragging(true);
      setDragStart({
        x: e.clientX - panPosition.x,
        y: e.clientY - panPosition.y
      });
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging && isZoomed) {
      const maxPanX = (imageRef.current?.offsetWidth * (zoomLevel - 1)) / 2;
      const maxPanY = (imageRef.current?.offsetHeight * (zoomLevel - 1)) / 2;
      
      const newX = e.clientX - dragStart.x;
      const newY = e.clientY - dragStart.y;

      setPanPosition({
        x: Math.max(Math.min(newX, maxPanX), -maxPanX),
        y: Math.max(Math.min(newY, maxPanY), -maxPanY)
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleWheel = (e) => {
    if (e.ctrlKey) {
      e.preventDefault();
      const delta = -Math.sign(e.deltaY) * 0.1;
      const newZoom = Math.max(1, Math.min(3, zoomLevel + delta));
      setZoomLevel(newZoom);
      if (newZoom > 1) {
        setIsZoomed(true);
        setIsAutoPlaying(false);
      } else {
        setIsZoomed(false);
        setPanPosition({ x: 0, y: 0 });
      }
    }
  };

  useEffect(() => {
    const handleKeyZoom = (e) => {
      if (e.ctrlKey && e.key === '+') {
        e.preventDefault();
        handleZoomIn();
      } else if (e.ctrlKey && e.key === '-') {
        e.preventDefault();
        handleZoomOut();
      }
    };

    window.addEventListener('keydown', handleKeyZoom);
    return () => window.removeEventListener('keydown', handleKeyZoom);
  }, []);

  const handleTouchStart = (e) => {
    if (e.touches.length === 2) {
      // Handle pinch zoom
      const distance = Math.hypot(
        e.touches[0].clientX - e.touches[1].clientX,
        e.touches[0].clientY - e.touches[1].clientY
      );
      setPinchStart({ distance, zoom: zoomLevel });
    } else {
      setTouchStart(e.touches[0].clientX);
      setTouchMoveCount(0);
      
      if (touchTimeout.current) {
        clearTimeout(touchTimeout.current);
      }
    }
  };

  const handleTouchMove = (e) => {
    if (pinchStart && e.touches.length === 2) {
      // Handle pinch zoom
      e.preventDefault();
      const distance = Math.hypot(
        e.touches[0].clientX - e.touches[1].clientX,
        e.touches[0].clientY - e.touches[1].clientY
      );
      
      const scale = distance / pinchStart.distance;
      const newZoom = Math.max(1, Math.min(3, pinchStart.zoom * scale));
      
      setZoomLevel(newZoom);
      if (newZoom > 1) {
        setIsZoomed(true);
        setIsAutoPlaying(false);
      }
    } else if (e.touches.length === 1) {
      setTouchEnd(e.touches[0].clientX);
      setTouchMoveCount(prev => prev + 1);
    }
  };

  const handleTouchEnd = (e) => {
    if (pinchStart) {
      setPinchStart(null);
      if (zoomLevel === 1) {
        setIsZoomed(false);
        setPanPosition({ x: 0, y: 0 });
      }
    } else {
      if (touchMoveCount < 3) {
        // Handle tap
        touchTimeout.current = setTimeout(() => {
          const touch = e.changedTouches[0];
          const element = document.elementFromPoint(touch.clientX, touch.clientY);
          const hotspotElement = element.closest(`.${styles.hotspot}`);
          
          if (hotspotElement) {
            const hotspotId = hotspotElement.dataset.id;
            setActiveHotspot(activeHotspot === hotspotId ? null : hotspotId);
          } else {
            setActiveHotspot(null);
          }
        }, 300);
      } else {
        // Handle swipe
        if (!touchStart || !touchEnd) return;
        
        const distance = touchStart - touchEnd;
        const isLeftSwipe = distance > 50;
        const isRightSwipe = distance < -50;

        if (isLeftSwipe) {
          nextSlide();
        } else if (isRightSwipe) {
          prevSlide();
        }
      }
    }
    
    setTouchStart(null);
    setTouchEnd(null);
  };

  useEffect(() => {
    const slider = sliderRef.current;
    if (slider) {
      slider.focus();
    }
  }, []);

  const getSlideClassName = (index) => {
    if (index === currentSlide) return styles.active;
    if (direction === 'next' && index === (currentSlide + 1) % slides.length) return styles.next;
    if (direction === 'prev' && index === (currentSlide - 1 + slides.length) % slides.length) return styles.prev;
    return '';
  };

  const handleQuickView = (productId) => {
    setActiveQuickView(activeQuickView === productId ? null : productId);
    setSelectedSize(null);
    setSelectedColor(null);
    setIs360View(false);
    setRotationDegree(0);
  };

  const handle360Drag = (e) => {
    if (is360View && rotationRef.current) {
      const rect = rotationRef.current.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const percentage = x / rect.width;
      const newDegree = Math.floor(percentage * 360);
      setRotationDegree(newDegree);
    }
  };

  const toggleView = () => {
    setIs360View(!is360View);
    setRotationDegree(0);
  };

  const renderQuickView = (product) => {
    if (!product) return null;

    return (
      <div className={styles.quickView} onClick={(e) => e.stopPropagation()}>
        <div className={styles.quickViewContent}>
          <button 
            className={styles.closeButton}
            onClick={() => setActiveQuickView(null)}
            aria-label="Close quick view"
          >
            <ion-icon name="close-outline"></ion-icon>
          </button>

          <div className={styles.productGallery}>
            {is360View ? (
              <div 
                ref={rotationRef}
                className={styles.view360}
                onMouseMove={handle360Drag}
              >
                <img 
                  src={product.images360[Math.floor(rotationDegree / (360 / product.images360.length))]}
                  alt={`${product.name} - ${rotationDegree}Â° view`}
                />
                <button 
                  className={styles.viewToggle}
                  onClick={toggleView}
                  aria-label="Switch to standard view"
                >
                  <ion-icon name="image-outline"></ion-icon>
                </button>
              </div>
            ) : (
              <div className={styles.standardView}>
                <img src={product.image} alt={product.name} />
                <button 
                  className={styles.viewToggle}
                  onClick={toggleView}
                  aria-label="Switch to 360Â° view"
                >
                  <ion-icon name="360-outline"></ion-icon>
                </button>
              </div>
            )}
          </div>

          <div className={styles.productInfo}>
            <h3>{product.name}</h3>
            <div className={styles.priceWrapper}>
              <span className={styles.price}>${product.price}</span>
              {product.originalPrice && (
                <span className={styles.originalPrice}>${product.originalPrice}</span>
              )}
            </div>

            <div className={styles.sizeSelector}>
              <h4>Select Size</h4>
              <div className={styles.sizeOptions}>
                {product.sizes.map(size => (
                  <button
                    key={size}
                    className={`${styles.sizeOption} ${selectedSize === size ? styles.selected : ''}`}
                    onClick={() => setSelectedSize(size)}
                  >
                    {size}
                  </button>
                ))}
              </div>
            </div>

            <div className={styles.colorSelector}>
              <h4>Select Color</h4>
              <div className={styles.colorOptions}>
                {product.colors.map(color => (
                  <button
                    key={color.name}
                    className={`${styles.colorOption} ${selectedColor === color.name ? styles.selected : ''}`}
                    style={{ '--color': color.code }}
                    onClick={() => setSelectedColor(color.name)}
                    aria-label={`Select ${color.name} color`}
                  >
                    {selectedColor === color.name && (
                      <ion-icon name="checkmark-outline"></ion-icon>
                    )}
                  </button>
                ))}
              </div>
            </div>

            <div className={styles.features}>
              {product.features.map((feature, index) => (
                <div key={index} className={styles.feature}>
                  <ion-icon name="checkmark-circle-outline"></ion-icon>
                  <span>{feature}</span>
                </div>
              ))}
            </div>

            <button 
              className={styles.addToCart}
              disabled={!selectedSize || !selectedColor}
            >
              Add to Cart
              <ion-icon name="bag-add-outline"></ion-icon>
            </button>
          </div>
        </div>
      </div>
    );
  };

  const renderHotspots = (slide) => {
    if (!slide.hotspots) return null;

    return slide.hotspots.map(hotspot => (
      <div
        key={hotspot.id}
        className={`${styles.hotspot} ${styles[hotspot.type]} ${activeHotspot === hotspot.id ? styles.active : ''}`}
        style={{ left: `${hotspot.x}%`, top: `${hotspot.y}%` }}
        onClick={(e) => {
          e.stopPropagation();
          if (hotspot.type === '360view') {
            setIs360View(true);
          } else {
            setActiveHotspot(activeHotspot === hotspot.id ? null : hotspot.id);
          }
        }}
        data-id={hotspot.id}
      >
        <div className={styles.hotspotIcon}>
          <ion-icon name={hotspot.icon}></ion-icon>
        </div>
        <div className={styles.hotspotContent}>
          <h3>{hotspot.title}</h3>
          <p>{hotspot.description}</p>
          {hotspot.price && (
            <button 
              className={styles.quickViewButton}
              onClick={(e) => {
                e.stopPropagation();
                handleQuickView(slide.id);
              }}
            >
              Quick View
              <ion-icon name="eye-outline"></ion-icon>
            </button>
          )}
        </div>
        <div className={styles.hotspotRing}></div>
      </div>
    ));
  };

  return (
    <section 
      ref={sliderRef}
      className={styles.hero}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onKeyDown={handleKeyDown}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      onWheel={handleWheel}
      tabIndex="0"
      role="region"
      aria-label="Hero Slider"
      style={{ cursor: isDragging ? 'grabbing' : isZoomed ? 'grab' : 'default' }}
    >
      <div className={styles.slider}>
        {slides.map((slide, index) => (
          <div
            key={slide.id}
            className={`${styles.slide} ${getSlideClassName(index)}`}
            style={{
              '--slide-color': slide.color
            }}
            aria-hidden={index !== currentSlide}
          >
            <div className={styles.content}>
              <span className={styles.subtitle} role="text">{slide.subtitle}</span>
              <h1 className={styles.title}>{slide.title}</h1>
              <p className={styles.description}>{slide.description}</p>
              <Link 
                to="/shop" 
                className={styles.cta}
                aria-label={`${slide.cta} - ${slide.title}`}
              >
                {slide.cta}
                <ion-icon name="arrow-forward-outline" aria-hidden="true"></ion-icon>
              </Link>
            </div>
            <div 
              ref={imageRef}
              className={`${styles.imageWrapper} ${isZoomed ? styles.zoomed : ''}`}
              style={{ 
                backgroundImage: `url(${is360View && index === currentSlide && slide.product?.images360 ? 
                  slide.product.images360[Math.floor(rotationDegree / (360 / slide.product.images360.length))] : 
                  slide.image})`,
                transform: isZoomed ? 
                  `scale(${zoomLevel}) translate(${panPosition.x}px, ${panPosition.y}px) translateZ(-50px)` : 
                  'scale(1.1) translateZ(-50px)'
              }}
              role="img"
              aria-label={`${slide.title} showcase image`}
            >
              <div className={styles.overlay} aria-hidden="true" />
              {renderHotspots(slide)}
            </div>
            {activeQuickView === slide.id && slide.product && renderQuickView(slide.product)}
          </div>
        ))}
      </div>

      <div className={styles.zoomControl}>
        <button
          className={styles.zoomButton}
          onClick={handleZoomIn}
          disabled={zoomLevel >= 3}
          aria-label="Zoom in"
        >
          <ion-icon name="add-outline"></ion-icon>
        </button>
        <button
          className={styles.zoomButton}
          onClick={handleZoomOut}
          disabled={zoomLevel <= 1}
          aria-label="Zoom out"
        >
          <ion-icon name="remove-outline"></ion-icon>
        </button>
        {isZoomed && (
          <button
            className={styles.zoomButton}
            onClick={() => {
              setZoomLevel(1);
              setIsZoomed(false);
              setPanPosition({ x: 0, y: 0 });
            }}
            aria-label="Reset zoom"
          >
            <ion-icon name="refresh-outline"></ion-icon>
          </button>
        )}
      </div>

      <div className={styles.previewContainer}>
        {slides.map((slide, index) => (
          <button
            key={slide.id}
            className={`${styles.preview} ${index === currentSlide ? styles.active : ''}`}
            onClick={() => goToSlide(index)}
            aria-label={`Preview of ${slide.title}`}
          >
            <img 
              src={slide.image} 
              alt="" 
              className={styles.previewImage}
              loading="lazy"
            />
            <div className={styles.previewOverlay} />
          </button>
        ))}
      </div>

      <div className={styles.controls} role="group" aria-label="Slider controls">
        <button 
          className={`${styles.arrow} ${styles.prev}`}
          onClick={prevSlide}
          aria-label="Previous slide"
        >
          <ion-icon name="chevron-back-outline" aria-hidden="true"></ion-icon>
        </button>
        <div className={styles.indicators} role="group" aria-label="Slide indicators">
          {slides.map((_, index) => (
            <button
              key={index}
              className={`${styles.indicator} ${index === currentSlide ? styles.active : ''}`}
              onClick={() => goToSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
              aria-current={index === currentSlide}
            />
          ))}
        </div>
        <button 
          className={`${styles.arrow} ${styles.next}`}
          onClick={nextSlide}
          aria-label="Next slide"
        >
          <ion-icon name="chevron-forward-outline" aria-hidden="true"></ion-icon>
        </button>
      </div>

      <div className={styles.progressBar}>
        <div 
          className={styles.progressFill} 
          style={{ width: `${progress}%` }}
        />
      </div>
    </section>
  );
};

export default Hero;
